        private string idiomaAtual; // Variável para armazenar o idioma atual

        public MenuPT(string idioma)
        {
            InitializeComponent();
            idiomaAtual = idioma; // Inicia a variável idiomaAtual com o valor passado no parâmetro
            AtualizarTextos(); // Atualiza os textos com base no idioma atual
            AtualizarBandeira(); // Atualize a imagem da bandeira com base no idioma atual
        }

        private void AtualizarTextos()
        {
            // Verifica o idioma atual e define os textos correspondentes para cada Label
            if (idiomaAtual == "ingles")
            {
                label1.Text = "Resistors";
                label2.Text = "Theorems";
                label3.Text = "Capacitors";
                label4.Text = "Coils";
                label5.Text = "Digital Signals";
                label6.Text = "Theorems";
            }
            else if (idiomaAtual == "portugues")
            {
                label1.Text = "Resistências";
                label2.Text = "Teoremas";
                label3.Text = "Condensadores";
                label4.Text = "Bobines";
                label5.Text = "Sinais Digitais";
                label6.Text = "Teoremas";
            }
           
        }

        private void AtualizarBandeira()
        {
            // Defina a imagem da PictureBox com base no idioma atual
            if (idiomaAtual == "ingles")
            {
                pictureBox7.Image = Properties.Resources.bandeira_portugues;
            }
            else if (idiomaAtual == "portugues")
            {
                pictureBox7.Image = Properties.Resources.bandeira_ingles;
            }
         
        }

        private void pictureBox7_Click(object sender, EventArgs e)
        {
            // Alterne o idioma ao clicar na bandeira
            if (idiomaAtual == "ingles")
            {
                idiomaAtual = "portugues";
            }
            else if (idiomaAtual == "portugues")
            {
                idiomaAtual = "ingles";
            }
            // Atualize os textos e a imagem da bandeira
            AtualizarTextos();
            AtualizarBandeira();
        }
